import React, { useState } from "react";
import { useLocation } from "react-router-dom";
import { IDevice } from "../context/DevicesContext";
import { RiLockPasswordLine } from "react-icons/ri";
import { FaDollarSign } from "react-icons/fa";
import DeviceFinder from "../apis/DeviceFinder";
import Navigation from "../components/Navigation";
import { useNavigate } from "react-router-dom";

// Component to display a payment method with its logo
interface PaymentMethodProps {
  name: string;
  logo: string;
}

const PaymentMethod: React.FC<PaymentMethodProps> = ({ name, logo }) => {
  return (
    <div className="col">
      <div className="p-3 payment-method">
        <label htmlFor={name}>
          <img
            className="payment-method-logo"
            src={logo}
            alt={name}
            style={{ width: "80px", height: "auto" }}
          />
          <input type="checkbox" id={name} name={name} value={name} />
        </label>
      </div>
    </div>
  );
};

const CheckoutPage = () => {
//  window.scrollTo(0, 0);
  const location = useLocation();
  const { cart } = location.state as { cart: IDevice[] };

  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [address, setAddress] = useState("");
  const [creditCard, setCreditCard] = useState("");
  const [password, setPassword] = useState("");
  const navigation = useNavigate();

  const getTotalPrice = () => {
    const totalPrice = cart.reduce((sum: number, device: IDevice) => {
      return sum + device.quantity! * +device.price;
    }, 0);

    return totalPrice.toFixed(2);
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    try {
      const response = await DeviceFinder.post("/users", {
        username: name,
        email,
        address,
        creditCard,
        password,
      });

      console.log(response);

      setName("");
      setEmail("");
      setAddress("");
      setCreditCard("");
      setPassword("");
    } catch (error) {
      console.error(error);
    }
  };

  const handleThank = () => {
    navigation("/Thankyou");
  };

  return (
    <>
      <Navigation source={"/FinalVideo.mp4"} title={"Checkout"} />
      <div className="container">
        <div className="card price-card" style={{ bottom: 0, right: 0 }}>
          <div className="card-body">
            <h5 className="card-title">Total price</h5>
            <div className="d-flex align-items-center">
              <FaDollarSign className="total-price-icon" />
              <div className="total-price-text">
                Total price: {getTotalPrice()}â‚¬
              </div>
            </div>
          </div>
        </div>

        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="name">Name</label>
            <input
              type="text"
              className="form-control"
              id="name"
              value={name}
              onChange={(e) => setName(e.target.value)}
            />
          </div>
          <div className="form-group">
            <label htmlFor="email">Email</label>
            <input
              type="email"
              className="form-control"
              id="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
          </div>
          <div className="form-group">
            <label htmlFor="address">Address</label>
            <input
              type="text"
              className="form-control"
              id="address"
              value={address}
              onChange={(e) => setAddress(e.target.value)}
            />
          </div>
          <div className="form-group">
            <label htmlFor="creditCard">Credit Card</label>
            <input
              type="text"
              className="form-control"
              id="creditCard"
              value={creditCard}
              onChange={(e) => setCreditCard(e.target.value)}
            />
          </div>
          <div className="form-group">
            <label htmlFor="password">Password</label>
            <div className="password-input">
              <input
                type="password"
                className="form-control"
                id="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
              <RiLockPasswordLine className="password-icon" />
            </div>
          </div>
          <div className="payment-methods">
            <label>Payment Method:</label>
            <div className="container text-center">
              <div className="row row-cols-2 row-cols-lg-5 g-2 g-lg-3">
                <PaymentMethod
                  name="PayPal"
                  logo="data:image/png;base64,"
                />
                <PaymentMethod
                  name="Visa"
                  logo="data:image/png;base64,"
                />
                <PaymentMethod
                  name="Mastercard"
                  logo="data:image/png;base64,"
                />
                {/* Add more payment methods here */}
                <button
                  onClick={handleThank}
                  type="submit"
                  className="btn btn-primary"
                  style={{
                    bottom: 0,
                    right: 0,
                    width: "300px",
                    height: "50px",
                  }}
                >
                  {" "}
                  Submit
                </button>
              </div>
            </div>
          </div>
        </form>
      </div>
    </>
  );
};

export default CheckoutPage;
